import React, { useState } from "react";
import { ThemeProvider } from "styled-components";
import { lightTheme, darkTheme } from "./StyledElements/theme";
import { GlobalStyles } from "./StyledElements/global";
import { ToggleContainer } from "./StyledElements/ToggleStyled";

// The function that toggles between themes
export default function App() {
  const [theme, setTheme] = useState("light");
  const toggleTheme = () => {
    if (theme === "light") {
      setTheme("dark");
    } else {
      setTheme("light");
    }
  };

  return (
    <ThemeProvider theme={theme === "light" ? lightTheme : darkTheme}>
      <GlobalStyles />
      <h1>It's a light theme!</h1>
      <ToggleContainer onClick={toggleTheme}>Dark Mode</ToggleContainer>
      <footer></footer>
    </ThemeProvider>
  );
}
