export const lightTheme = {
  body: "#fafafa",
  text: "#363537",
  toggleBorder: "#c4c4c4",
  gradient:
    "linear-gradient(to top, #030303, #3d3d3d, #787878, #b9b9b9, #ffffff)",
};

export const darkTheme = {
  body: "#333",
  text: "#FAFAFA",
  toggleBorder: "#6B8096",
  gradient:
    "linear-gradient(90deg, hsla(216, 41%, 79%, 1) 0%, hsla(186, 33%, 94%, 1) 100%)",
};
