import React from "react";
import Meaning from "./Meaning";
import ImageGrid from "./ImagesGrid";
import Phonetics from "./Phonetics";
import {
  StyledParagraph,
  Word,
  StyledRow,
  StyledCol,
  StyledContainer,
  StyledWord,
} from "../styled-components/Descriptions-styling";

export default function Descriptions(props) {
  if (props.data) {
    return (
      <StyledRow className='descriptions'>
        <StyledWord>{props.data.word}</StyledWord>
        <StyledParagraph>{props.data.phonetics[0].text}</StyledParagraph>
        <Phonetics audios={props.data.phonetics} />
        {props.data.meanings.map(function (meaning, index) {
          return (
            <div key={index}>
              <Meaning data={meaning} />
            </div>
          );
        })}

        <ImageGrid images={props.images} />
      </StyledRow>
    );
  } else {
    return null;
  }
}
