import React, { useState } from "react";
import { ThemeProvider } from "styled-components";
import { lightTheme, darkTheme } from "./styled-components/theme";
import { GlobalStyles } from "./styled-components/global";
import { ToggleContainer } from "./styled-components/ToggleStyled";
import { IconImage } from "./styled-components/styledIconImage";
import sunIcon from "./icons/sun.png";

// The function that toggles between themes
export default function App() {
  const [theme, setTheme] = useState("light");
  function toggleTheme() {
    if (theme === "light") {
      setTheme("dark");
      let toggleIcon = sunIcon;
    } else {
      setTheme("light");
    }
  }

  return (
    <ThemeProvider theme={theme === "light" ? lightTheme : darkTheme}>
      <GlobalStyles />
      <h1>React Dictionary</h1>
      <ToggleContainer onClick={toggleTheme}>
        <IconImage src={toggleIcon} alt='toggle icon' />
      </ToggleContainer>
      <footer></footer>
    </ThemeProvider>
  );
}
