import styled from "styled-components";
export const StyledContainer = styled.div`
  width: 100%;
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;
`;

export const StyledRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-right: -15px;
  margin-left: -15px;
  margin-top: 15px;
`;

export const StyledCol = styled.div`
  position: relative;
  width: 100%;
  padding-right: 15px;
  padding-left: 15px;
`;

export const StyledWord = styled.h1`
  font-size: 2.2em;
  font-weight: 600;
  text-transform: capitalize;
  display: inline;
`;

export const StyledParagraph = styled.p`
  font-size: 1.2em;
  color: ${({ theme }) => theme.secondaryColor};
`;
