import styled from "styled-components";

export const Input = styled.input`
  border-radius: 5px;
  border: ${({ theme }) => theme.borderInput};
  width: 350px;
  padding: 5px;
  margin: 0 10px;
  background-color: ${({ theme }) => theme.background};
`;

export const SearchBtn = styled.button`
  border-radius: 5px;
  padding: 5px 10px;
  border: 2px solid #ff7082;
  background: #ff7082;
  color: #fafafa;
  font-family: "Archivo";
`;
